import {GoogleGenAI, Modality} from '@google/genai';

const ai = new GoogleGenAI({apiKey: process.env.API_KEY});

// --- UI ELEMENTS ---
const sourceImage = document.getElementById('source-image') as HTMLImageElement;
const promptInput = document.getElementById('prompt-input') as HTMLInputElement;
const generateButton = document.getElementById('generate-button') as HTMLButtonElement;
const imageGallery = document.getElementById('image-gallery') as HTMLElement;
const buttonText = document.querySelector('.button-text') as HTMLElement;
const spinner = document.querySelector('.spinner') as HTMLElement;

// --- SOURCE IMAGE DATA (Base64 encoded) ---
const sourceImageMimeType = 'image/jpeg';
// This is the base64 representation of the first image you provided.
const sourceImageBase64 = '/9j/4AAQSkZJRgABAQAAAQABAAD/2wBDAAYEBAUEBAYFBQUGBgYHCQ4JCQgICRINDQoOFRIWFhUSFBQXGI_--lightboxes';

// --- LIGHTBOX STATE ---
let imageSources: string[] = [];
let currentIndex = 0;

// --- LIGHTBOX UI ELEMENTS ---
const modal = document.getElementById('lightbox-modal') as HTMLElement;
const modalImage = document.getElementById('lightbox-image') as HTMLImageElement;
const closeButton = document.querySelector('.close-button') as HTMLElement;
const prevButton = document.querySelector('.prev-button') as HTMLElement;
const nextButton = document.querySelector('.next-button') as HTMLElement;
const downloadButton = document.getElementById('download-button') as HTMLAnchorElement;


function updateLightboxImage(newIndex: number) {
    currentIndex = newIndex;
    const currentImageSrc = imageSources[currentIndex];
    
    modalImage.src = currentImageSrc;
    modalImage.alt = `Generated Image ${currentIndex + 1}`;
    
    downloadButton.href = currentImageSrc;
    
    // Extract file extension from mime type for a better filename
    const mimeType = currentImageSrc.match(/data:([a-zA-Z0-9]+\/[a-zA-Z0-9-.+]+).*,.*/);
    const extension = mimeType && mimeType.length > 1 ? mimeType[1].split('/')[1] : 'png';
    downloadButton.download = `edited-image-${currentIndex + 1}.${extension}`;
}

function showModal(index: number) {
    if (imageSources.length > 0 && modal && modalImage && downloadButton) {
        updateLightboxImage(index);
        modal.classList.remove('hidden');
    }
}

function closeModal() {
    if (modal) {
        modal.classList.add('hidden');
    }
}

function showNextImage() {
    if (imageSources.length === 0) return;
    const newIndex = (currentIndex + 1) % imageSources.length;
    updateLightboxImage(newIndex);
}

function showPrevImage() {
    if (imageSources.length === 0) return;
    const newIndex = (currentIndex - 1 + imageSources.length) % imageSources.length;
    updateLightboxImage(newIndex);
}

function setButtonLoadingState(isLoading: boolean) {
    generateButton.disabled = isLoading;
    if (isLoading) {
        buttonText.classList.add('hidden');
        spinner.classList.remove('hidden');
    } else {
        buttonText.classList.remove('hidden');
        spinner.classList.add('hidden');
    }
}

async function editImage() {
    const prompt = promptInput.value;
    if (!prompt) {
        alert('Please enter a prompt.');
        return;
    }

    setButtonLoadingState(true);
    imageGallery.innerHTML = '<p class="placeholder">Generating your image... please wait.</p>';

    try {
        const response = await ai.models.generateContent({
            model: 'gemini-2.5-flash-image-preview',
            contents: {
                parts: [
                    {
                        inlineData: {
                            data: sourceImageBase64,
                            mimeType: sourceImageMimeType,
                        },
                    },
                    {
                        text: prompt,
                    },
                ],
            },
            config: {
                responseModalities: [Modality.IMAGE, Modality.TEXT],
            },
        });

        imageGallery.textContent = '';
        imageSources = [];

        if (response.candidates && response.candidates[0].content.parts) {
            let imageFound = false;
            response.candidates[0].content.parts.forEach((part, index) => {
                if (part.inlineData) {
                    imageFound = true;
                    const src = `data:${part.inlineData.mimeType};base64,${part.inlineData.data}`;
                    imageSources.push(src);

                    const img = new Image();
                    img.src = src;
                    img.alt = `${prompt} - Image ${imageSources.length}`;

                    img.addEventListener('click', () => {
                        showModal(imageSources.length - 1);
                    });
                    
                    imageGallery.appendChild(img);
                }
            });
            if (!imageFound) {
                 imageGallery.innerHTML = '<p class="placeholder">The model did not return an image. Try a different prompt.</p>';
            }
        } else {
             imageGallery.innerHTML = '<p class="placeholder">Could not generate image. No valid response received.</p>';
        }

    } catch (error) {
        console.error("Error generating images:", error);
        imageGallery.innerHTML = `<p class="placeholder" style="color: red;">Error: Could not generate image. Check the console for details.</p>`;
    } finally {
        setButtonLoadingState(false);
    }
}


function initializeApp() {
    if (!sourceImage || !promptInput || !generateButton || !imageGallery || !modal || !closeButton || !nextButton || !prevButton || !downloadButton) {
        console.error('A required UI element is missing from the DOM.');
        return;
    }

    // Set source image and default prompt
    sourceImage.src = `data:${sourceImageMimeType};base64,${sourceImageBase64}`;
    promptInput.value = 'Add a birthday hat and some balloons';

    // --- EVENT LISTENERS ---
    generateButton.addEventListener('click', editImage);

    closeButton.addEventListener('click', closeModal);
    nextButton.addEventListener('click', showNextImage);
    prevButton.addEventListener('click', showPrevImage);

    window.addEventListener('keydown', (e) => {
        if (!modal.classList.contains('hidden')) {
            if (e.key === 'Escape') closeModal();
            else if (e.key === 'ArrowRight') showNextImage();
            else if (e.key === 'ArrowLeft') showPrevImage();
        }
    });
}

initializeApp();